--
-- Table structure for table `protection`
--

CREATE TABLE `protection` (
  `id` int(11) NOT NULL,
  `detail` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `protection`
--

INSERT INTO `protection` (`id`, `detail`) VALUES
(1, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- รักษาความสะอาดของร่างกายอย่างสม่ำเสมอ</p></li><li><p>- หลีกเลี่ยงการใช้ของส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- รักษาความสะอาดของสัตว์เลี้ยง</p></li><li><p>- สวมเสื้อผ้าที่แห้งและระบายอากาศได้ดี</p></li><li><p>- หลีกเลี่ยงการสัมผัสกับสัตว์หรือบุคคลที่ติดเชื้อ</p></li></ul>'),
(2, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- ลดการบริโภคเกลือและอาหารที่มีไขมันสูง</p></li><li><p>- ควบคุมน้ำหนักให้อยู่ในเกณฑ์</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอ</p></li><li><p>- งดสูบบุหรี่และลดการดื่มแอลกอฮอล์</p></li><li><p>- ลดความเครียดและนอนหลับให้เพียงพอ</p></li><li><p>- ตรวจวัดความดันโลหิตเป็นประจำ</p></li></ul>'),
(3, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- ฉีดวัคซีนป้องกันอีสุกอีใสและวัคซีนป้องกันงูสวัดในผู้สูงอายุหรือกลุ่มเสี่ยง</p></li><li><p>- หลีกเลี่ยงการสัมผัสกับตุ่มน้ำของผู้ป่วยงูสวัด</p></li><li><p>- รักษาความสะอาดบริเวณผื่น งดเกาหรือแกะตุ่มน้ำ</p></li><li><p>- ดูแลสุขภาพให้แข็งแรงเพื่อเสริมภูมิคุ้มกัน</p></li></ul>'),
(4, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- หลีกเลี่ยงการสัมผัสสารที่อาจแพ้ เช่น สบู่ แชมพู หรือสารเคมีบางชนิด</p></li><li><p>- รักษาความสะอาดของผิวหนังและรักษาความชุ่มชื้น</p></li><li><p>- ใส่เสื้อผ้าที่ไม่รัดแน่น ระบายอากาศได้ดี</p></li><li><p>- หลีกเลี่ยงการเกาจนผิวหนังเป็นแผล</p></li><li><p>- ปรึกษาแพทย์เมื่อผื่นคันไม่หาย หรือมีอาการรุนแรง</p></li></ul>'),
(5, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- รักษาความสะอาดของผิวหนัง โดยเฉพาะบริเวณที่มีแผลหรือบาดเจ็บ</p></li><li><p>- หลีกเลี่ยงการเกาหรือบีบฝีด้วยมือสกปรก</p></li><li><p>- ใช้อุปกรณ์ส่วนตัว เช่น ผ้าเช็ดตัว ผ้าปูที่นอน ที่สะอาด</p></li><li><p>- รักษาแผลหรือบาดแผลให้สะอาดและป้องกันการติดเชื้อ</p></li><li><p>- หากมีแผลหรือฝีที่ไม่ดีขึ้น ควรพบแพทย์เพื่อรับการรักษาที่เหมาะสม</p></li></ul>'),
(6, '<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- หลีกเลี่ยงสิ่งกระตุ้นที่ทำให้เกิดลมพิษ เช่น อาหารบางชนิด ยา แมลงสัตว์ หรือความเครียด</p></li><li><p>- สวมเสื้อผ้าที่ระบายอากาศได้ดี</p></li><li><p>- งดการใช้ผลิตภัณฑ์ที่อาจระคายเคืองผิวหนัง</p></li><li><p>- หากทราบสาเหตุ ควรหลีกเลี่ยงอย่างเคร่งครัด</p></li></ul>'),
(7, '<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ</p></li><li><p>- ดื่มน้ำมากๆ</p></li><li><p>- รับประทานยาแก้ปวดลดไข้ (paracetamol)</p></li><li><p>- ทายา Calamine lotion เพื่อบรรเทาอาการคัน</p></li><li><p>- หลีกเลี่ยงการแกะเกาตุ่มเพื่อป้องกันการติดเชื้อแทรกซ้อนและรอยแผลเป็น</p></li><li><p>- กักตัวเพื่อป้องกันการแพร่เชื้อ</p></li><li><p>- วัคซีนป้องกันอีสุกอีใส</p></li></ul>'),
(8, '<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ ดื่มน้ำมากๆ</p></li><li><p>- รับประทานอาหารที่มีประโยชน์</p></li><li><p>- ล้างมือบ่อยๆ ด้วยสบู่หรือเจลแอลกอฮอล์</p></li><li><p>- สวมหน้ากากอนามัยเมื่ออยู่ในที่สาธารณะ</p></li><li><p>- หลีกเลี่ยงการสัมผัสใกล้ชิดกับผู้ป่วย</p></li><li><p>- ฉีดวัคซีนป้องกันไข้หวัดใหญ่ประจำปี</p></li></ul>'),
(9, '<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- รักษาความสะอาดบริเวณแผลด้วยน้ำสะอาดและสบู่อ่อน</p></li><li><p>- ป้องกันไม่ให้แผลถูกเกาหรือเปิดออก</p></li><li><p>- ใช้ผ้าปิดแผลหรือพลาสเตอร์ที่สะอาดเพื่อป้องกันการติดเชื้อ</p></li><li><p>- หากมีอาการอักเสบหรือหนอง ควรพบแพทย์เพื่อรับการรักษา</p></li><li><p>- หลีกเลี่ยงการใช้ของส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- รักษาความชุ่มชื้นของผิวเพื่อป้องกันการเกิดแผลพุพองจากผิวแห้ง</p></li></ul>'),
(10, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ป้องกันไม่ให้ยุงกัด เช่น ใช้มุ้ง ล้างแหล่งน้ำขังกำจัดลูกน้ำยุง</p></li><li><p>- ดื่มน้ำและพักผ่อนให้เพียงพอ</p></li><li><p>- หากมีไข้สูงและอาการรุนแรงควรพบแพทย์ทันที</p></li><li><p>- ไม่ควรใช้ยาที่ทำให้เลือดแข็งตัว เช่น แอสไพริน โดยไม่ได้รับคำแนะนำแพทย์</p></li><li><p>- ฉีดวัคซีนป้องกันไข้เลือดออกในกลุ่มที่มีความเสี่ยง </p></li></ul>'),
(11, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รักษาความสะอาดบริเวณตาและมือ</p></li><li><p>- หลีกเลี่ยงการขยี้หรือเกาเปลือกตา</p></li><li><p>- ใช้ผ้าชุบน้ำอุ่นประคบบริเวณตากุ้งยิงวันละหลายครั้งเพื่อช่วยลดอาการบวมและเร่งให้ฝีแตก</p></li><li><p>- ไม่ใช้ของส่วนตัวร่วมกับผู้อื่น เช่น ผ้าเช็ดหน้า</p></li><li><p>- หากอาการรุนแรงหรือไม่ดีขึ้น ควรพบแพทย์เพื่อรับการรักษาเพิ่มเติม</p></li></ul>'),
(12, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงการสัมผัสแผลหรือของเหลวจากตุ่มน้ำของผู้ติดเชื้อ</p></li><li><p>- ใช้ถุงยางอนามัยเมื่อมีเพศสัมพันธ์เพื่อลดความเสี่ยง</p></li><li><p>- รักษาความสะอาดบริเวณที่เป็นแผล</p></li><li><p>- หลีกเลี่ยงการใช้ของใช้ส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- พบแพทย์เพื่อรับยาต้านไวรัสเมื่อมีอาการหรือตามคำแนะนำ</p></li></ul>'),
(13, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงสารก่อภูมิแพ้ เช่น ฝุ่น เกสรดอกไม้ และขนสัตว์</p></li><li><p>- ทำความสะอาดบ้านและพื้นที่อยู่อาศัยอย่างสม่ำเสมอ</p></li><li><p>- ใช้เครื่องฟอกอากาศเพื่อลดสารก่อภูมิแพ้ในอากาศ</p></li><li><p>- ใช้น้ำเกลือล้างจมูกเพื่อลดอาการคัดจมูก</p></li><li><p>- ปรึกษาแพทย์เพื่อรับยารักษาและคำแนะนำเพิ่มเติม</p></li></ul>'),
(14, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนให้เพียงพอและดื่มน้ำมาก ๆ</p></li><li><p>- ใช้ผ้าชุบน้ำอุ่นประคบเพื่อลดอาการปวดและบวม</p></li><li><p>- รับประทานอาหารอ่อนหรือของเหลวเพื่อไม่ให้เจ็บคอมาก</p></li><li><p>- หลีกเลี่ยงการสัมผัสใกล้ชิดกับผู้อื่นในช่วงที่มีอาการ</p></li><li><p>- ฉีดวัคซีน MMR (วัคซีนป้องกันหัด คางทูม และหัดเยอรมัน) ตามคำแนะนำของแพทย์</p></li></ul>'),
(15, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ</p></li><li><p>- ดื่มน้ำอุ่นหรือของเหลวมาก ๆ เพื่อบรรเทาอาการเจ็บคอ</p></li><li><p>- กลั้วคอด้วยน้ำเกลือเพื่อบรรเทาอาการอักเสบ</p></li><li><p>- หลีกเลี่ยงการใช้ของใช้ส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- หากติดเชื้อแบคทีเรีย ควรรับประทานยาปฏิชีวนะตามแพทย์สั่งจนหมด</p></li><li><p>- หมั่นล้างมือและรักษาสุขอนามัยเพื่อลดโอกาสการติดเชื้อ</p></li></ul>'),
(16, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยให้มูกไม่เหนียว</p></li><li><p>- ล้างจมูกด้วยน้ำเกลือเพื่อช่วยลดอาการคัดจมูก</p></li><li><p>- หลีกเลี่ยงควัน ฝุ่น และสารก่อภูมิแพ้</p></li><li><p>- รักษาโรคภูมิแพ้หรือหวัดให้หายเร็ว ไม่ปล่อยให้เรื้อรัง</p></li><li><p>- หากมีอาการรุนแรงหรือเป็นบ่อย ควรปรึกษาแพทย์เพื่อวินิจฉัยและรักษา</p></li><li><p>- ในบางรายอาจต้องใช้ยาปฏิชีวนะ หรือยาพ่นจมูกตามคำแนะนำแพทย์</p></li></ul>'),
(17, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- แปรงฟันอย่างถูกวิธีอย่างน้อยวันละ 2 ครั้ง</p></li><li><p>- ใช้ไหมขัดฟันหรือน้ำยาบ้วนปากช่วยกำจัดคราบแบคทีเรีย</p></li><li><p>- หมั่นตรวจสุขภาพช่องปากกับทันตแพทย์ทุก 6 เดือน</p></li><li><p>- หลีกเลี่ยงอาหารที่มีน้ำตาลสูงซึ่งเพิ่มโอกาสเกิดคราบแบคทีเรีย</p></li><li><p>- งดสูบบุหรี่ และควบคุมโรคเบาหวานหากมี เพราะส่งผลต่อสุขภาพเหงือก</p></li></ul>'),
(18, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รับประทานอาหารที่มีไอโอดีนเพียงพอ เช่น เกลือเสริมไอโอดีน อาหารทะเล</p></li><li><p>- หมั่นตรวจสุขภาพโดยเฉพาะหากมีประวัติคอพอกในครอบครัว</p></li><li><p>- หลีกเลี่ยงสารที่อาจรบกวนการทำงานของต่อมไทรอยด์ เช่น ควันบุหรี่</p></li><li><p>- หากมีอาการผิดปกติควรปรึกษาแพทย์เพื่อตรวจเลือดและอัลตราซาวด์</p></li><li><p>- การรักษาอาจใช้ยา รังสี หรือการผ่าตัด ขึ้นอยู่กับสาเหตุและขนาดของก้อน</p></li></ul>'),
(19, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ลุกขึ้นช้า ๆ จากท่านอนหรือท่านั่ง เพื่อป้องกันหน้ามืด</p></li><li><p>- ดื่มน้ำให้เพียงพออย่างน้อย 6–8 แก้วต่อวัน</p></li><li><p>- รับประทานอาหารให้ครบหมู่ และทานอาหารมื้อเล็ก ๆ บ่อยครั้ง</p></li><li><p>- หลีกเลี่ยงแอลกอฮอล์ และการยืนนาน ๆ โดยไม่ขยับตัว</p></li><li><p>- ใส่ถุงรัดขา (compression stockings) เพื่อช่วยให้เลือดไหลเวียนดีขึ้นในรายที่เป็นเรื้อรัง</p></li><li><p>- หากมีอาการบ่อยหรือรุนแรง ควรปรึกษาแพทย์เพื่อตรวจหาสาเหตุและรับการรักษา</p></li></ul>'),
(20, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนสายตาและหลีกเลี่ยงการขยี้ตาแรง ๆ</p></li><li><p>- หลีกเลี่ยงกิจกรรมที่เพิ่มแรงดันในร่างกาย เช่น เบ่งแรง จามหรือไอแรง</p></li><li><p>- หากมีโรคประจำตัว เช่น ความดันโลหิตสูง ควรควบคุมให้ดี</p></li><li><p>- หากเกิดซ้ำบ่อย ควรปรึกษาแพทย์เพื่อตรวจดูการแข็งตัวของเลือดหรือโรคอื่น ๆ ที่เกี่ยวข้อง</p></li><li><p>- โดยทั่วไปไม่จำเป็นต้องใช้ยา ยกเว้นมีอาการระคายเคือง ซึ่งอาจใช้ยาหยอดตาตามแพทย์แนะนำ</p></li></ul>'),
(21, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำเกลือแร่ (ORS) เพื่อชดเชยการสูญเสียน้ำและเกลือแร่</p></li><li><p>- หลีกเลี่ยงอาหารมัน ของทอด อาหารดิบ และนมระหว่างมีอาการ</p></li><li><p>- พักผ่อนให้เพียงพอ และดูแลสุขอนามัยอย่างเคร่งครัด</p></li><li><p>- หมั่นล้างมือก่อนรับประทานอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- รับประทานอาหารที่ปรุงสุก สะอาด และดื่มน้ำต้มสุกหรือน้ำกรอง</p></li><li><p>- หากมีไข้สูง ถ่ายเป็นเลือด หรืออาการไม่ดีขึ้นใน 2–3 วัน ควรพบแพทย์</p></li></ul>'),
(22, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำเกลือแร่ (ORS) เพื่อทดแทนของเหลวและเกลือแร่ที่สูญเสียไป</p></li><li><p>- หลีกเลี่ยงการใช้ยาหยุดถ่ายโดยไม่จำเป็น เพราะอาจทำให้เชื้ออยู่ในลำไส้นานขึ้น</p></li><li><p>- รับประทานอาหารอ่อน ย่อยง่าย หลีกเลี่ยงของมัน ดิบ หรือเผ็ด</p></li><li><p>- ล้างมือให้สะอาดก่อนกินอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- กินอาหารปรุงสุก และดื่มน้ำสะอาด (น้ำต้มสุกหรือน้ำกรอง)</p></li><li><p>- หากมีไข้สูง ถ่ายเป็นเลือด อ่อนเพลียมาก หรืออาการไม่ดีขึ้นใน 1–2 วัน ควรรีบพบแพทย์ เพราะอาจต้องใช้ยาปฏิชีวนะเฉพาะ</p></li></ul>'),
(23, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ลดความเครียดด้วยการผ่อนคลาย เช่น การฝึกหายใจลึก ๆ หรือทำสมาธิ</p></li><li><p>- หลีกเลี่ยงเครื่องดื่มที่มีคาเฟอีน แอลกอฮอล์ และบุหรี่</p></li><li><p>- นอนหลับให้เพียงพอ และออกกำลังกายอย่างเหมาะสม</p></li><li><p>- ตรวจสุขภาพหัวใจอย่างสม่ำเสมอ โดยเฉพาะผู้ที่มีโรคประจำตัว เช่น ความดันโลหิตสูง เบาหวาน</p></li><li><p>- หากมีอาการบ่อยหรือรุนแรง ควรพบแพทย์เพื่อตรวจวินิจฉัยและรับการรักษา</p></li></ul>'),
(24, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ปรุงอาหารโดยเฉพาะเนื้อสัตว์ให้สุกทั่วถึงก่อนรับประทาน</p></li><li><p>- รักษาความสะอาดของอาหารและน้ำดื่ม</p></li><li><p>- ล้างมือให้สะอาดก่อนรับประทานอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- หากสงสัยว่าติดพยาธิ ควรพบแพทย์เพื่อตรวจวินิจฉัยและรับยาถ่ายพยาธิ</p></li><li><p>- ตรวจสุขภาพเป็นประจำโดยเฉพาะในกลุ่มเสี่ยง เช่น ผู้ที่อยู่ในพื้นที่ที่มีการระบาด</p></li></ul>'),
(25, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รักษาความสะอาดของมือให้ดี โดยล้างมือด้วยสบู่บ่อย ๆ โดยเฉพาะก่อนกินอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- ตัดเล็บให้สั้นและสะอาด เพื่อลดการเกาและแพร่ไข่พยาธิ</p></li><li><p>- ซักผ้าปูที่นอน เสื้อผ้า และผ้าขนหนูด้วยน้ำร้อน เพื่อกำจัดไข่พยาธิ</p></li><li><p>- หลีกเลี่ยงการเกาบริเวณทวารหนัก</p></li><li><p>- รับประทานยาถ่ายพยาธิตามคำแนะนำของแพทย์ และอาจต้องรักษาทั้งครอบครัวพร้อมกันเพื่อลดการติดเชื้อซ้ำ</p></li><li><p>- ทำความสะอาดสิ่งของใช้ส่วนตัวและทำความสะอาดบ้านอย่างสม่ำเสมอ</p></li></ul>'),
(26, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยล้างแบคทีเรียออกจากระบบทางเดินปัสสาวะ</p></li><li><p>- ปัสสาวะเมื่อรู้สึกต้องการ อย่ากลั้นปัสสาวะนาน</p></li><li><p>- รักษาความสะอาดบริเวณอวัยวะเพศ โดยเช็ดจากด้านหน้าไปด้านหลัง</p></li><li><p>- หลีกเลี่ยงการใช้สบู่หรือผลิตภัณฑ์ที่ระคายเคืองบริเวณอวัยวะเพศ</p></li><li><p>- สวมใส่เสื้อผ้าที่ระบายอากาศได้ดี</p></li><li><p>- หากมีอาการควรไปพบแพทย์เพื่อรับการตรวจและรักษาด้วยยาปฏิชีวนะอย่างเหมาะสม</p></li><li><p>- ในบางราย แพทย์อาจแนะนำให้ดื่มน้ำแครนเบอร์รีเพื่อช่วยป้องกันการติดเชื้อซ้ำ</p></li></ul>'),
(27, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ใช้ถุงยางอนามัยทุกครั้งเมื่อมีเพศสัมพันธ์</p></li><li><p>- จำกัดจำนวนคู่รักทางเพศ และตรวจสุขภาพทางเพศอย่างสม่ำเสมอ</p></li><li><p>- หลีกเลี่ยงการใช้สารเสพติดที่อาจทำให้ความสามารถในการตัดสินใจลดลง</p></li><li><p>- หลีกเลี่ยงการมีเพศสัมพันธ์กับผู้ที่มีอาการติดเชื้อหรือมีประวัติโรคติดต่อทางเพศ</p></li><li><p>- หากสงสัยว่าติดเชื้อ ควรพบแพทย์เพื่อรับการตรวจและรักษาอย่างถูกต้อง</p></li><li><p>- แจ้งคู่รักให้ตรวจรักษาพร้อมกันเพื่อป้องกันการติดเชื้อซ้ำ</p></li><li><p>- รักษาความสะอาดของอวัยวะเพศและสุขอนามัยส่วนตัว</p></li></ul>'),
(28, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงอาหารที่มีกรดยูริกสูง เช่น เครื่องในสัตว์ เนื้อแดง อาหารทะเล หอย และเครื่องดื่มที่มีแอลกอฮอล์</p></li><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยขับกรดยูริกออกทางปัสสาวะ</p></li><li><p>- ควบคุมน้ำหนักให้อยู่ในเกณฑ์ปกติ</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอและพักผ่อนเพียงพอ</p></li><li><p>- รับประทานยาแก้อักเสบหรือยาลดกรดยูริกตามคำแนะนำของแพทย์</p></li><li><p>- หลีกเลี่ยงการดื่มแอลกอฮอล์และเครื่องดื่มที่มีน้ำตาลสูง</p></li></ul>'),
(29, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ และรับประทานอาหารที่มีกากใยสูง เช่น ผัก ผลไม้ เพื่อป้องกันท้องผูก</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอเพื่อช่วยระบบขับถ่าย</p></li><li><p>- หลีกเลี่ยงการกลั้นอุจจาระหรือเบ่งถ่ายแรงเกินไป</p></li><li><p>- หลีกเลี่ยงการนั่งหรือนั่งยองนานเกินไป</p></li><li><p>- ใช้ยาทา หรือยาสอดที่แพทย์แนะนำเพื่อลดอาการอักเสบและบวม</p></li><li><p>- หากอาการรุนแรงควรพบแพทย์เพื่อรับการรักษาเพิ่มเติม เช่น การผ่าตัด</p></li></ul>'),
(30, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงการเกาจมูกแรง ๆ หรือใส่อะไรแหย่จมูก</p></li><li><p>- รักษาความชุ่มชื้นของโพรงจมูก เช่น ใช้น้ำเกลือล้างจมูก</p></li><li><p>- หลีกเลี่ยงสิ่งกระตุ้นที่ก่อให้เกิดภูมิแพ้ เช่น ฝุ่น ควัน และสารเคมี</p></li><li><p>- หลีกเลี่ยงการอยู่ในที่แห้ง หรือมีอากาศเย็นจัดเกินไป</p></li><li><p>- หากมีเลือดออกจากจมูก ให้บีบจมูกช่วงปลายประมาณ 5-10 นาทีและก้มหน้าไปข้างหน้าเพื่อป้องกันการกลืนเลือด</p></li><li><p>- หากเลือดไหลไม่หยุดหรือมีเลือดออกมาก ควรพบแพทย์ทันที</p></li><li><p>- รักษาโรคภูมิแพ้หรือการติดเชื้ออย่างเหมาะสมตามคำแนะนำแพทย์</p></li></ul>'),
(31, '<p><strong>การปฐมพยาบาล :</strong></p><ul><li><p><strong>- ตรวจสอบความปลอดภัยของบริเวณเกิดเหตุ</strong> ก่อนเข้าไปช่วยเหลือ</p></li><li><p><strong>- ตรวจสอบสติและการหายใจ</strong> ของผู้บาดเจ็บ หากไม่หายใจให้ทำ CPR ทันที</p></li><li><p><strong>- หยุดเลือด</strong> โดยใช้ผ้าสะอาดกดปิดแผลอย่างเบา ๆ</p></li><li><p><strong>- ป้องกันการเคลื่อนไหวของคอและกระดูกสันหลัง</strong> โดยอย่าให้ผู้บาดเจ็บขยับตัวโดยไม่จำเป็น</p></li><li><p><strong>- ให้นอนราบและยกขาสูงเล็กน้อย</strong> หากไม่มีอาการบาดเจ็บที่ศีรษะหรือกระดูกสันหลัง เพื่อป้องกันช็อก</p></li><li><p><strong>- คลุมผู้บาดเจ็บด้วยผ้าห่ม</strong> เพื่อรักษาอุณหภูมิร่างกาย</p></li><li><p>- หากผู้บาดเจ็บมีบาดแผลเปิดให้ล้างแผลด้วยน้ำสะอาดและปิดด้วยผ้าสะอาด<br><br></p></li></ul><p><strong>การเคลื่อนย้ายผู้ป่วยในที่เกิดเหตุ :</strong></p><ul><li><p>- หากไม่มีความจำเป็นหรือไม่ได้รับการฝึกอบรม ไม่ควรเคลื่อนย้ายผู้บาดเจ็บโดยเฉพาะผู้ที่สงสัยว่ามีการบาดเจ็บที่กระดูกสันหลังหรือศีรษะ</p></li><li><p>- หากต้องเคลื่อนย้าย เช่น เพื่อความปลอดภัย (อัคคีภัย) ให้ใช้วิธี <strong>การเคลื่อนย้ายทั้งร่างกาย (spinal motion restriction)</strong> เช่น การใช้เสื่อหรือบอร์ดพยุงตัว</p></li><li><p>- ใช้วิธี <strong>ลากแบบสายพาน</strong> หรือ <strong>การจับพยุงตัวแบบสามคน</strong> หากไม่มีอุปกรณ์ช่วย</p></li><li><p>- ให้เคลื่อนย้ายอย่างระมัดระวัง ไม่บิดหรืองอคอและกระดูกสันหลัง</p></li><li><p>- รีบนำผู้บาดเจ็บส่งโรงพยาบาลโดยเร็ว</p></li></ul>'),
(32, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- อย่าเคลื่อนย้ายผู้บาดเจ็บหากไม่จำเป็นโดยเฉพาะถ้ามีอาการบาดเจ็บที่ศีรษะหรือกระดูกสันหลัง</p></li><li><p>- หลีกเลี่ยงการบิดหรืองอร่างกายผู้บาดเจ็บ</p></li><li><p>- หลีกเลี่ยงการยกหรือขยับผู้บาดเจ็บอย่างรุนแรง</p></li><li><p>- ถ้าต้องเคลื่อนย้ายให้ใช้วิธีช่วยเหลือแบบถูกต้อง เช่น การพยุงด้วยบอร์ดหรือเสื่อพยุง</p></li><li><p>- รีบนำส่งโรงพยาบาลโดยเร็ว</p></li><li><p>- ในชีวิตประจำวันควรระมัดระวังการใช้อุปกรณ์ที่สูง เช่น บันได และสวมอุปกรณ์ป้องกันเมื่อทำงานในที่สูง</p></li></ul>'),
(33, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การปฐมพยาบาลเบื้องต้น (หลัก R.I.C.E.):</strong></p><ul><li><p><strong>- Rest</strong>: หยุดใช้งานบริเวณที่บาดเจ็บ</p></li><li><p><strong>- Ice</strong>: ประคบเย็น 15-20 นาทีทุก 1-2 ชั่วโมง เพื่อลดบวม</p></li><li><p><strong>- Compression</strong>: ใช้ผ้ายืดพันเพื่อป้องกันอาการบวมมากขึ้น</p></li><li><p><strong>- Elevation</strong>: ยกบริเวณที่บาดเจ็บให้อยู่สูงกว่าระดับหัวใจ</p></li></ul>'),
(34, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การดูแลเบื้องต้น:</strong></p><ul><li><p>- ลองดื่มน้ำ หรือน้ำลายกลืนตามปกติ หากเป็นก้างเล็ก อาจหลุดเอง</p></li><li><p>- รับประทานอาหารที่นุ่มแต่มีแรงดัน เช่น ข้าวปั้นกลืนตาม หรือกล้วย (ห้ามใช้วิธีนี้หากเจ็บมากหรือกลืนไม่ได้)</p></li><li><p>- หลีกเลี่ยงการแหย่ลำคอด้วยมือหรือของแข็ง เพราะอาจทำให้แผลลึกหรือก้างฝังมากขึ้น</p></li><li><p>- หากก้างยังไม่หลุด หรือรู้สึกเจ็บมาก <strong>ควรไปพบแพทย์ทันที</strong> เพื่อให้ผู้เชี่ยวชาญนำออกอย่างปลอดภัย<br></p></li></ul><p><strong>การป้องกัน:</strong></p><ul><li><p>- เลือกปลาที่มีก้างน้อย หรือเอาก้างออกก่อนรับประทาน</p></li><li><p>- เคี้ยวอาหารให้ละเอียดก่อนกลืน</p></li><li><p>- ระมัดระวังโดยเฉพาะในเด็กหรือผู้สูงอายุ</p></li><li><p>- หลีกเลี่ยงการพูดหรือหัวเราะขณะกินปลา</p></li></ul>'),
(35, '<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การดูแลเบื้องต้น:</strong></p><ul><li><p>- ล้างแผลทันทีด้วยน้ำสะอาดหรือสบู่อ่อน ๆ อย่างน้อย 5 นาที</p></li><li><p>- ห้ามใช้แอลกอฮอล์หรือไฮโดรเจนเปอร์ออกไซด์โดยตรงกับแผลลึก เพราะอาจทำลายเนื้อเยื่อ</p></li><li><p>- กดแผลเพื่อห้ามเลือดด้วยผ้าสะอาดหรือผ้าก๊อซ</p></li><li><p>- ปิดแผลด้วยผ้าพันแผลที่สะอาด</p></li><li><p>- หากแผลลึกหรือเลือดไม่หยุด ควรรีบนำส่งโรงพยาบาล</p></li><li><p>- ควรพิจารณาฉีดวัคซีนบาดทะยักหากไม่ได้รับการกระตุ้นภายใน 5–10 ปี<br></p></li></ul><p><strong>การป้องกัน:</strong></p><ul><li><p>- ใช้ของมีคมอย่างระมัดระวัง และเก็บในที่ปลอดภัย</p></li><li><p>- สวมถุงมือป้องกันเมื่อต้องใช้เครื่องมือหรืออุปกรณ์แหลมคม</p></li><li><p>- ห้ามปล่อยเด็กเล่นของมีคมโดยเด็ดขาด</p></li><li><p>- หากของมีคมตกอยู่ ควรเก็บให้เรียบร้อยทันที</p></li></ul>');

-- --------------------------------------------------------

--
-- Table structure for table `situation`
--

CREATE TABLE `situation` (
  `id` int(11) NOT NULL,
  `detail` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `situation`
--

INSERT INTO `situation` (`id`, `detail`) VALUES
(1, '<h3><strong>การติดต่อ :</strong></h3><ul><li><p>ติดต่อได้ง่ายจากการสัมผัสผิวหนังของคนที่ติดเชื้อ</p></li><li><p>หรือสัมผัสสิ่งของที่มีเชื้อ เช่น ผ้า เสื้อผ้า ของใช้ส่วนตัว</p></li><li><p>สัตว์เลี้ยง เช่น แมวและสุนัขที่ติดเชื้อก็สามารถแพร่เชื้อได้</p></li></ul>'),
(2, '<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ความดันโลหิตสูง <strong>ไม่ติดต่อ</strong> จากคนสู่คน</p></li></ul>'),
(3, '<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- งูสวัด <strong>ไม่ติดต่อโดยตรงเป็นงูสวัด</strong> แต่ไวรัสที่อยู่ในตุ่มน้ำสามารถติดต่อไปสู่คนที่ไม่เคยติดเชื้ออีสุกอีใสมาก่อน ทำให้เกิดโรคอีสุกอีใสแทน</p></li><li><p>- การติดต่อเกิดจากการสัมผัสกับน้ำในตุ่มน้ำของผู้ป่วยงูสวัด</p></li><li><p>- ผู้ป่วยงูสวัดควรหลีกเลี่ยงการสัมผัสกับผู้ที่ไม่เคยเป็นอีสุกอีใสมาก่อน หรือผู้ที่มีภูมิคุ้มกันต่ำ</p></li></ul>'),
(4, '<h3><strong>การติดต่อ</strong></h3><ul><li><p><strong>ขึ้นอยู่กับสาเหตุของผื่น เช่น :</strong></p><ul><li><p>- หากเกิดจากการติดเชื้อ (เชื้อรา เชื้อแบคทีเรีย หรือไวรัส) อาจติดต่อได้ทางการสัมผัส</p></li><li><p>- หากเกิดจากการแพ้หรือโรคภูมิแพ้ จะไม่ติดต่อ</p></li></ul></li><li><p>- ควรตรวจสอบสาเหตุเพื่อประเมินการติดต่อได้อย่างถูกต้อง</p></li></ul>'),
(5, '<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ฝีโดยทั่วไป <strong>ไม่ติดต่อจากคนสู่คนโดยตรง</strong></p></li><li><p>- แต่เชื้อแบคทีเรียที่ทำให้เกิดฝีสามารถแพร่กระจายได้หากสัมผัสกับหนองหรือสารคัดหลั่งจากฝี โดยเฉพาะถ้าไม่มีการป้องกัน</p></li><li><p>- ควรระวังการสัมผัสกับแผลเปิดและทำความสะอาดมือบ่อย ๆ</p></li></ul>'),
(6, '<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ลมพิษ <strong>ไม่ติดต่อ</strong> จากคนสู่คน</p></li></ul>'),
(7, '<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อโดยการสัมผัสตรงกับตุ่มน้ำ หรือทางละอองฝอยจากการไอหรือจามของผู้ป่วย</p></li><li><p>- ติดต่อได้ง่ายและรวดเร็วในคนที่ไม่เคยเป็นหรือไม่เคยได้รับวัคซีน</p></li></ul>'),
(8, '<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม หรือการสัมผัสกับสิ่งของที่มีเชื้อไวรัส</p></li><li><p>- ติดต่อได้ง่ายในสถานที่แออัด</p></li></ul>'),
(9, '<p><strong>การติดต่อ :</strong></p><ul><li><p>- หากแผลพุพองเกิดจากเชื้อแบคทีเรียหรือไวรัสบางชนิด อาจติดต่อผ่านการสัมผัสกับของเหลวในตุ่มน้ำ</p></li><li><p>- ควรหลีกเลี่ยงการสัมผัสกับแผลหรือของเหลวจากแผลของผู้อื่น</p></li></ul>'),
(10, '<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อผ่านการถูกยุงลายที่มีเชื้อไวรัสกัด</p></li><li><p>- ไม่ติดต่อโดยตรงจากคนสู่คน</p></li></ul>'),
(11, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ตากุ้งยิงไม่ติดต่อจากคนสู่คนโดยตรง</p></li><li><p>- แต่แบคทีเรียที่ทำให้เกิดตากุ้งยิงสามารถแพร่ผ่านการสัมผัสมือสกปรกหรือการใช้ของส่วนตัวร่วมกันได้</p></li></ul>'),
(12, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านการสัมผัสโดยตรงกับแผลหรือตุ่มน้ำของผู้ติดเชื้อ</p></li><li><p>- ติดต่อได้ทั้งทางการจูบ สัมผัสผิวหนัง หรือทางเพศสัมพันธ์</p></li></ul>'),
(13, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ติดต่อจากคนสู่คน</p></li></ul>'),
(14, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม หรือการสัมผัสใกล้ชิดกับผู้ป่วย</p></li><li><p>- ติดต่อได้ง่ายในสถานที่แออัด เช่น โรงเรียน</p></li></ul>'),
(15, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม</p></li><li><p>- สัมผัสกับน้ำลายหรือสารคัดหลั่งของผู้ป่วย เช่น การใช้แก้วน้ำหรือช้อนร่วมกัน</p></li></ul>'),
(16, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้เองไม่ติดต่อโดยตรง</p></li><li><p>- แต่อาจเกิดตามหลังโรคติดเชื้อทางเดินหายใจส่วนบน เช่น หวัด หรือไข้หวัด ซึ่งสามารถติดต่อได้</p></li></ul>'),
(17, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ติดต่อโดยตรงจากคนสู่คน</p></li><li><p>- แต่แบคทีเรียในช่องปากสามารถถ่ายทอดได้ทางการใช้ของใช้ร่วมกัน เช่น แปรงสีฟัน หรือการจูบ</p></li></ul>'),
(18, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคคอพอกไม่ใช่โรคติดต่อ</p></li><li><p>- แต่สาเหตุบางอย่างอาจพบในคนในครอบครัวเดียวกัน เช่น โรคภูมิคุ้มกันทำลายต่อมไทรอยด์</p></li></ul>'),
(19, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อ</p></li><li><p>- แต่สามารถเกิดร่วมกับภาวะหรือโรคอื่น เช่น ภาวะขาดน้ำ หัวใจเต้นผิดจังหวะ หรือใช้ยาบางชนิด</p></li></ul>'),
(20, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อ</p></li><li><p>- เป็นเพียงภาวะเลือดออกใต้เยื่อบุตาจากการกระทบกระเทือนหรือแรงดันในร่างกาย</p></li></ul>'),
(21, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อได้ทางการรับประทานอาหารหรือดื่มน้ำที่ปนเปื้อนเชื้อโรค</p></li><li><p>- การสัมผัสกับอุจจาระของผู้ป่วย หรือของใช้ที่มีเชื้อปนเปื้อน</p></li><li><p>- พบมากในพื้นที่ที่สุขอนามัยไม่ดี</p></li></ul>'),
(22, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการรับประทานอาหารหรือน้ำที่ปนเปื้อนเชื้อโรค</p></li><li><p>- ติดต่อจากการสัมผัสกับอุจจาระของผู้ติดเชื้อโดยตรง หรือของใช้ที่ปนเปื้อน</p></li><li><p>- ติดต่อได้ง่ายในสถานที่ที่แออัดหรือสุขาภิบาลไม่ดี เช่น โรงเรียน ศูนย์เด็กเล็ก ค่ายผู้อพยพ</p></li></ul>'),
(23, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคใจสั่นใจหวิวไม่ใช่โรคติดต่อ</p></li><li><p>- เป็นอาการที่เกิดจากปัจจัยภายในร่างกายหรือภายนอก เช่น ความเครียด หรือโรคประจำตัว</p></li></ul>'),
(24, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการรับประทานอาหารหรือน้ำที่ปนเปื้อนไข่หรือตัวอ่อนของพยาธิตัวตืด</p></li><li><p>- อาจเกิดจากการบริโภคเนื้อสัตว์ที่ปรุงไม่สุก เช่น เนื้อวัว เนื้อหมู หรือปลาที่มีตัวพยาธิ</p></li></ul>'),
(25, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการกลืนไข่พยาธิที่ปนเปื้อนมากับมือ อาหาร ของเล่น หรือผ้าปูที่นอน</p></li><li><p>- การเกาแล้วนำมือเข้าปากทำให้เกิดการติดเชื้อซ้ำ (cycle of reinfection)</p></li><li><p>- ติดต่อกันง่ายในกลุ่มเด็กหรือในครอบครัว</p></li></ul>'),
(26, '<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อทางเพศสัมพันธ์โดยตรง แต่การมีเพศสัมพันธ์อาจเพิ่มความเสี่ยงในการติดเชื้อ</p></li><li><p>- ติดต่อจากแบคทีเรียที่ปนเปื้อนในท่อปัสสาวะและแพร่เข้าสู่กระเพาะปัสสาวะ</p></li></ul>'),
(27, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านการมีเพศสัมพันธ์โดยไม่ป้องกันกับผู้ที่ติดเชื้อ</p></li><li><p>- การสัมผัสกับเลือด หรือของเหลวในร่างกายที่มีเชื้อ</p></li><li><p>- จากแม่สู่ลูกระหว่างการคลอดในบางโรค</p></li></ul>'),
(28, '<p><strong>การติดต่อ</strong></p><ul><li><p>- เกาต์ไม่ใช่โรคติดต่อ</p></li><li><p>- เกิดจากภาวะภายในร่างกาย เช่น การสะสมกรดยูริกสูงจากการกินอาหารบางชนิด หรือปัญหาเมตาบอลิซึม</p></li></ul>'),
(29, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ริดสีดวงทวารไม่ใช่โรคติดต่อ</p></li><li><p>- เกิดจากปัจจัยภายในร่างกายและพฤติกรรมการใช้ชีวิต</p></li></ul>'),
(30, '<p><strong>การติดต่อ</strong></p><ul><li><p>- อาการถ่ายมูกและมูกเลือดไม่ใช่โรคติดต่อ แต่สาเหตุที่เป็นเชื้อไวรัสหรือแบคทีเรียที่ทำให้เกิดการติดเชื้อในจมูกอาจติดต่อกันได้</p></li><li><p>- ภูมิแพ้เองไม่ติดต่อ</p></li></ul>'),
(31, '<p><strong>การติดต่อ</strong></p><ul><li><p>- อุบัติเหตุรถล้มไม่ใช่โรคติดต่อ</p></li><li><p>- แต่ควรระมัดระวังการติดเชื้อจากบาดแผล เช่น แผลเปิดหรือเลือดที่สัมผัสกับผู้ช่วยเหลือ</p></li><li><p>- ผู้ช่วยเหลือควรใช้ถุงมือหรืออุปกรณ์ป้องกันเพื่อลดความเสี่ยงการติดเชื้อจากสารคัดหลั่งหรือเลือด</p></li></ul>'),
(32, '<p><strong>การติดต่อ</strong></p><ul><li><p>- เหตุการณ์ตกจากที่สูงไม่ใช่โรคติดต่อ</p></li><li><p>- แต่ควรระวังการติดเชื้อจากบาดแผล หากมีแผลเปิดหรือเลือดสัมผัสผู้ช่วยเหลือ</p></li><li><p>- ควรใช้ถุงมือหรืออุปกรณ์ป้องกันเมื่อต้องสัมผัสผู้บาดเจ็บ</p></li></ul>'),
(33, '<p><strong>การติดต่อ</strong></p><ul><li><p>- การบาดเจ็บจากการเล่นกีฬาไม่ใช่โรคติดต่อ</p></li><li><p>- อย่างไรก็ตาม หากมีแผลเปิด ควรระมัดระวังการสัมผัสเลือดหรือน้ำเหลืองเพื่อลดความเสี่ยงการติดเชื้อ</p></li><li><p>- ผู้ช่วยเหลือควรใช้ถุงมือหากต้องสัมผัสแผลหรือเลือด</p></li></ul>'),
(34, '<p><strong>การติดต่อ</strong></p><ul><li><p>- ก้างปลาติดคอไม่ใช่ภาวะติดต่อ</p></li><li><p>- แต่ถ้ามีแผลหรือเลือด ควรหลีกเลี่ยงการสัมผัสโดยตรงเพื่อป้องกันการติดเชื้อจากน้ำลายหรือเลือด</p></li></ul>'),
(35, '<p><strong>การติดต่อ</strong></p><ul><li><p>- แผลจากของมีคม <strong>ไม่ใช่โรคติดต่อโดยตรง</strong></p></li><li><p>- แต่ควรระวังการสัมผัสเลือดหรือสารคัดหลั่งจากแผล เพราะอาจเป็นพาหะของโรคติดต่อทางเลือด เช่น ไวรัสตับอักเสบ B, C หรือ HIV</p></li><li><p>- ผู้ให้ความช่วยเหลือควรสวมถุงมือยางเสมอ</p></li></ul>');

-- --------------------------------------------------------

--
-- Table structure for table `symptom`
--

CREATE TABLE `symptom` (
  `id` int(11) NOT NULL,
  `detail` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `symptom`
--

INSERT INTO `symptom` (`id`, `detail`) VALUES
(1, '<h3><strong>อาการ :</strong></h3><ul><li><p>มีผื่นแดงเป็นวงกลมขอบนูน ชัดเจน</p></li><li><p>ผื่นจะมีขอบเขตชัดเจนเป็นวงคล้ายวงแหวน (จึงเรียก Ringworm)</p></li><li><p>บางครั้งมีอาการคันร่วมด้วย</p></li><li><p>บางรายอาจมีสะเก็ดหรือผิวหนังลอกบริเวณแผล</p></li><li><p>สามารถเกิดได้ตามส่วนต่าง ๆ ของร่างกาย เช่น แขน ขา หนังศีรษะ หรือบริเวณขาหนีบ</p></li></ul>'),
(2, '<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- มักไม่มีอาการในช่วงแรก (โรคเงียบ)</p></li><li><p>- อาจมีอาการปวดศีรษะ เวียนศีรษะ หายใจลำบาก ใจสั่นในบางราย</p></li><li><p>- หากไม่ได้รับการรักษา อาจเกิดภาวะแทรกซ้อนรุนแรง เช่น หัวใจวาย หลอดเลือดสมองแตก</p></li></ul>'),
(3, '<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- ปวดแสบ ปวดร้อน หรือรู้สึกชาตามแนวเส้นประสาทที่ไวรัสกลับมา</p></li><li><p>- ผื่นแดงขึ้นเป็นกลุ่ม แล้วพัฒนาเป็นตุ่มน้ำใส (พุพอง)</p></li><li><p>- ตุ่มน้ำแตกแล้วตกสะเก็ด</p></li><li><p>- ผื่นมักขึ้นตามด้านใดด้านหนึ่งของร่างกาย เช่น หน้าอก หลัง หรือใบหน้า</p></li><li><p>- อาจมีไข้ ปวดศีรษะ อ่อนเพลียร่วมด้วย</p></li></ul>'),
(4, '<h3><strong>อาการของโรค</strong></h3><ul><li><p>- มีผื่นแดงขึ้นตามผิวหนัง บางครั้งเป็นตุ่มน้ำ ตุ่มหนอง หรือลอกเป็นสะเก็ด</p></li><li><p>- รู้สึกคันมากจนต้องเกา</p></li><li><p>- ผิวหนังอาจบวม หรือมีรอยถลอกจากการเกา</p></li><li><p>- อาการอาจเป็นเฉพาะที่หรือทั่วตัว</p></li><li><p>- อาจมีอาการร่วม เช่น แสบร้อน หรือเจ็บเล็กน้อย</p></li></ul>'),
(5, '<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- ก้อนนูน บวม แดง และเจ็บปวดบริเวณผิวหนัง</p></li><li><p>- บางครั้งมีผิวหนังบริเวณฝีรอบ ๆ ร้อนและบวมมากขึ้น</p></li><li><p>- อาจมีหนองสะสมใต้ผิวหนัง และบางครั้งฝีจะเปิดออกแล้วมีหนองไหลออกมา</p></li><li><p>- อาจมีไข้ หนาวสั่น หรืออ่อนเพลีย หากการติดเชื้อรุนแรง</p></li><li><p>- อาการเจ็บจะเพิ่มขึ้นเมื่อกดหรือเคลื่อนไหวบริเวณนั้น</p></li></ul>'),
(6, '<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- มีตุ่มนูนแดงหรือผื่นขึ้นบนผิวหนังอย่างรวดเร็ว</p></li><li><p>- มีอาการคันมาก บางครั้งรู้สึกแสบร้อนหรือเจ็บ</p></li><li><p>- ผื่นอาจขึ้นเป็นวงหรือแผ่ขยายไปทั่วร่างกาย</p></li><li><p>- อาการมักหายไปภายใน 24 ชั่วโมง แต่มักเกิดขึ้นซ้ำได้</p></li></ul>'),
(7, '<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีไข้ต่ำ ๆ หรือสูงก่อนเกิดผื่น</p></li><li><p>- ผื่นเริ่มจากตุ่มแดงเล็ก ๆ แล้วพัฒนาเป็นตุ่มน้ำใส</p></li><li><p>- ตุ่มน้ำแตกและตกสะเก็ดภายในไม่กี่วัน</p></li><li><p>- คันมากและมักขึ้นทั่วตัว รวมถึงใบหน้าและหนังศีรษะ</p></li><li><p>- อาจมีอาการเหนื่อย เพลียร่วมด้วย</p></li></ul>'),
(8, '<p><strong>อาการของโรค :</strong></p><ul><li><p>- ไข้สูงเฉียบพลัน หนาวสั่น</p></li><li><p>- ปวดศีรษะ ปวดเมื่อยกล้ามเนื้อ</p></li><li><p>- ไอแห้ง เจ็บคอ น้ำมูกไหล</p></li><li><p>- อ่อนเพลีย เหนื่อยง่าย</p></li><li><p>- บางรายอาจมีอาการคลื่นไส้ อาเจียน</p></li></ul>'),
(9, '<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีตุ่มน้ำหรือพุพองที่ผิวหนัง บางครั้งเจ็บหรือแสบร้อน</p></li><li><p>- พุพองอาจแตกและมีของเหลวไหลออกมา</p></li><li><p>- บริเวณรอบแผลอาจแดง บวม และเจ็บ</p></li><li><p>- อาจมีหนองหรือกลิ่นไม่พึงประสงค์ถ้าเกิดการติดเชื้อ</p></li><li><p>- อาจมีไข้ร่วมในกรณีที่ติดเชื้อรุนแรง</p></li></ul>'),
(10, '<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีไข้สูงอย่างรวดเร็วประมาณ 2-7 วัน</p></li><li><p>- ปวดศีรษะอย่างรุนแรง</p></li><li><p>- ปวดข้อและกล้ามเนื้อ</p></li><li><p>- มีจุดเลือดออกหรือผื่นแดงตามผิวหนัง</p></li><li><p>- เลือดออกตามเหงือก จมูก หรือมีเลือดออกในระบบทางเดินอาหารในรายที่รุนแรง</p></li><li><p>- อ่อนเพลียมาก</p></li></ul>'),
(11, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีตุ่มนูนแดง เจ็บและบวมที่ขอบเปลือกตา</p></li><li><p>- รู้สึกระคายเคืองหรือแสบตา</p></li><li><p>- บางครั้งมีน้ำตาไหลหรือรู้สึกหนักตา</p></li><li><p>- ตุ่มอาจแตกและมีหนองไหลออกมาในระยะต่อมา</p></li></ul>'),
(12, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีตุ่มน้ำใสเล็ก ๆ รวมกันเป็นกลุ่ม บริเวณปาก ใบหน้า หรืออวัยวะเพศ</p></li><li><p>- ตุ่มน้ำจะแตกเป็นแผลเจ็บและตกสะเก็ดภายใน 1-2 สัปดาห์</p></li><li><p>- อาจมีอาการปวด แสบ หรือคันบริเวณที่เป็น</p></li><li><p>- ในบางรายอาจมีไข้ ปวดเมื่อยกล้ามเนื้อ และต่อมน้ำเหลืองบวมร่วมด้วย</p></li></ul>'),
(13, '<p><strong>อาการของโรค</strong></p><ul><li><p>- คัดจมูก แน่นจมูก หายใจลำบาก</p></li><li><p>- น้ำมูกใสไหลตลอดเวลา</p></li><li><p>- จามบ่อย และมีอาการคันจมูก คอ และตา</p></li><li><p>- ตาแดงหรือแสบตาในบางราย</p></li><li><p>- บางครั้งอาจมีอาการปวดหัวหรือรู้สึกเมื่อยล้า</p></li></ul>'),
(14, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีไข้ต่ำถึงปานกลาง</p></li><li><p>- ปวดและบวมบริเวณใต้กรามหรือแก้มด้านใดด้านหนึ่งหรือทั้งสองข้าง</p></li><li><p>- เจ็บเวลากลืนน้ำลายหรือเคี้ยวอาหาร</p></li><li><p>- บางรายอาจมีอาการปวดศีรษะ อ่อนเพลีย และเบื่ออาหารร่วมด้วย</p></li></ul>'),
(15, '<p><strong>อาการของโรค</strong></p><ul><li><p>- เจ็บคอ โดยเฉพาะเวลากลืนอาหาร</p></li><li><p>- คอบวมแดง มีจุดหนองสีขาวหรือเหลืองที่ต่อมทอนซิล</p></li><li><p>- มีไข้ หนาวสั่น</p></li><li><p>- มีกลิ่นปาก</p></li><li><p>- เสียงเปลี่ยนหรือพูดไม่ชัดเพราะคอบวม</p></li><li><p>- ต่อมน้ำเหลืองบริเวณคอบวมและเจ็บ</p></li></ul>'),
(16, '<p><strong>อาการของโรค</strong></p><ul><li><p>- คัดจมูกและมีน้ำมูกข้นเหนียว อาจมีสีเขียวหรือเหลือง</p></li><li><p>- ปวดหรือกดเจ็บบริเวณหน้าผาก โหนกแก้ม รอบดวงตา หรือฐานจมูก</p></li><li><p>- ปวดศีรษะ โดยเฉพาะเมื่อลุกขึ้นนั่งหรือก้มศีรษะ</p></li><li><p>- มีไข้ (ในบางราย)</p></li><li><p>- กลิ่นปากไม่พึงประสงค์</p></li><li><p>- อาจมีอาการไอ โดยเฉพาะเวลากลางคืน</p></li></ul>'),
(17, '<p><strong>อาการของโรค</strong></p><ul><li><p>- เหงือกบวม แดง และเจ็บ</p></li><li><p>- มีเลือดออกขณะแปรงฟันหรือใช้ไหมขัดฟัน</p></li><li><p>- มีกลิ่นปาก</p></li><li><p>- เหงือกร่นหรือแยกตัวออกจากฟัน</p></li><li><p>- ในบางรายอาจมีอาการเสียวฟันร่วมด้วย</p></li></ul>'),
(18, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีก้อนหรือต่อมนูนบริเวณด้านหน้าลำคอ อาจโตขึ้นเรื่อย ๆ</p></li><li><p>- รู้สึกแน่นคอหรือหายใจลำบาก (ในรายที่ก้อนโตมาก)</p></li><li><p>- เสียงแหบหรือกลืนอาหารลำบาก</p></li><li><p>- อาจมีอาการร่วมของภาวะไทรอยด์ทำงานผิดปกติ เช่น</p><ul><li><p><strong>ไทรอยด์ทำงานเกิน</strong>: ใจสั่น เหงื่อออก น้ำหนักลด ขี้ร้อน</p></li><li><p><strong>ไทรอยด์ทำงานน้อย</strong>: อ่อนเพลีย น้ำหนักขึ้น หนาวง่าย</p></li></ul></li></ul>'),
(19, '<p><strong>อาการของโรค</strong></p><ul><li><p>- เวียนศีรษะ หรือรู้สึกโคลงเคลงเมื่อเปลี่ยนอิริยาบถ เช่น ลุกขึ้นยืน</p></li><li><p>- หน้ามืด ตาพร่า หรือเห็นดาว</p></li><li><p>- เหนื่อยง่าย อ่อนเพลีย</p></li><li><p>- หัวใจเต้นเร็วหรือผิดจังหวะ</p></li><li><p>- เป็นลมหรือหมดสติในบางกรณี</p></li><li><p>- ผิวหนังเย็น ชื้น และซีด</p></li></ul>'),
(20, '<p><strong>อาการของโรค</strong></p><ul><li><p>- เห็นเป็นจุดหรือปื้นแดงบนตาขาว (ไม่เจ็บ ไม่เคืองตา)</p></li><li><p>- ไม่มีอาการตามัวหรือปวดตา</p></li><li><p>- ตาอาจรู้สึกตึงหรือรำคาญเล็กน้อยในบางราย</p></li><li><p>- ไม่มีน้ำตาไหลหรือตาแดงแบบอักเสบทั่วไป</p></li></ul>'),
(21, '<p><strong>อาการของโรค</strong></p><ul><li><p>- ถ่ายอุจจาระเหลวหรือเป็นน้ำ</p></li><li><p>- ปวดท้องหรือปวดบิด</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- อ่อนเพลีย</p></li><li><p>- อาจมีไข้ หรืออุจจาระมีมูกเลือดในบางกรณี</p></li><li><p>- หากรุนแรง อาจมีอาการขาดน้ำ เช่น ปากแห้ง ปัสสาวะน้อย หน้ามืด</p></li></ul>'),
(22, '<p><strong>อาการของโรค</strong></p><ul><li><p>- ถ่ายเหลว มีมูกหรือเลือดปนในอุจจาระ</p></li><li><p>- ปวดเบ่งถ่าย (tenesmus) หรือรู้สึกอยากถ่ายบ่อย ๆ แม้ไม่มีอุจจาระ</p></li><li><p>- ปวดท้องหรือปวดบิด</p></li><li><p>- มีไข้ หนาวสั่น</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- หากรุนแรง อาจทำให้เกิดภาวะขาดน้ำหรือช็อกได้</p></li></ul>'),
(23, '<p><strong>อาการของโรค</strong></p><ul><li><p>- รู้สึกหัวใจเต้นเร็ว หรือเต้นแรงกว่าปกติ</p></li><li><p>- หัวใจเต้นไม่สม่ำเสมอ หรือเต้นกระตุก</p></li><li><p>- รู้สึกใจหวิวหรือวูบวาบในอก</p></li><li><p>- บางรายอาจมีอาการเวียนศีรษะ หรือเป็นลมวูบร่วมด้วย</p></li><li><p>- อาจรู้สึกเหนื่อยหรือหายใจลำบาก</p></li></ul>'),
(24, '<p><strong>อาการของโรค</strong></p><ul><li><p>- ปวดท้อง ท้องอืด หรือรู้สึกไม่สบายท้อง</p></li><li><p>- เบื่ออาหาร น้ำหนักลดโดยไม่ทราบสาเหตุ</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- อุจจาระอาจพบปล้องพยาธิหรือไข่พยาธิ</p></li><li><p>- อ่อนเพลียหรือมีอาการซีดจากภาวะโลหิตจางในบางราย</p></li><li><p>- ในบางกรณีอาจไม่มีอาการชัดเจน</p></li></ul>'),
(25, '<p><strong>อาการของโรค</strong></p><ul><li><p>- คันรุนแรงบริเวณทวารหนัก โดยเฉพาะในตอนกลางคืน</p></li><li><p>- นอนไม่หลับหรือหลับไม่สนิท</p></li><li><p>- อาจมีอาการระคายเคืองบริเวณทวารหนัก หรือพบผื่นแดง</p></li><li><p>- อาจมีอาการปวดท้องเบา ๆ หรือเบื่ออาหารในบางราย</p></li><li><p>- ในเด็กอาจพบการหงุดหงิดหรือกังวลใจ</p></li></ul>'),
(26, '<p><strong>อาการของโรค</strong></p><ul><li><p>- ปัสสาวะขัด ปัสสาวะบ่อยและเร่งด่วน</p></li><li><p>- ปัสสาวะมีกลิ่นเหม็นหรือขุ่น</p></li><li><p>- ปวดหรือแสบขณะปัสสาวะ</p></li><li><p>- ปวดท้องน้อยหรือรู้สึกแน่นบริเวณกระเพาะปัสสาวะ</p></li><li><p>- อาจมีไข้ต่ำ ๆ หรือหนาวสั่นในบางราย</p></li><li><p>- ในบางกรณีอาจมีเลือดปนในปัสสาวะ</p></li></ul>'),
(27, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีอาการตกขาวหรือของเหลวจากอวัยวะเพศที่ผิดปกติ</p></li><li><p>- ปวดหรือแสบขณะปัสสาวะ</p></li><li><p>- มีแผลหรือผื่นที่อวัยวะเพศหรือบริเวณรอบ ๆ</p></li><li><p>- บางรายอาจมีอาการเจ็บปวดบริเวณอวัยวะเพศหรือท้องน้อย</p></li><li><p>- อาการอาจไม่แสดงในบางคน แต่ยังสามารถแพร่เชื้อได้</p></li><li><p>- อาการอื่น ๆ เช่น ไข้ ต่อมน้ำเหลืองโต หรือรู้สึกไม่สบายทั่วไป</p></li></ul>'),
(28, '<p><strong>อาการของโรค</strong></p><ul><li><p>- ปวดข้ออย่างรุนแรงและเฉียบพลัน โดยเฉพาะข้อหัวแม่เท้า</p></li><li><p>- ข้อบวม แดง และร้อนบริเวณที่ปวด</p></li><li><p>- มีอาการปวดมากในช่วงกลางคืนหรือหลังทานอาหารที่มีกรดยูริกสูง</p></li><li><p>- อาจมีไข้ร่วมด้วยในบางราย</p></li><li><p>- ในบางกรณีอาจเกิดก้อนแข็ง (โทฟัส) ตามข้อหรือเนื้อเยื่อรอบ ๆ</p></li></ul>'),
(29, '<p><strong>อาการของโรค</strong></p><ul><li><p>- มีเลือดสดปนกับอุจจาระหรือหยดหลังถ่าย</p></li><li><p>- รู้สึกเจ็บหรือคันบริเวณทวารหนัก</p></li><li><p>- มีตุ่มบวมหรือก้อนบริเวณทวารหนัก</p></li><li><p>- อาจรู้สึกไม่สบายเวลานั่งหรือเดิน</p></li><li><p>- ในกรณีรุนแรงอาจมีอาการปวดมากและบวมอย่างชัดเจน</p></li></ul>'),
(30, '<p><strong>อาการของโรค</strong></p><ul><li><p>- น้ำมูกไหล อาจเป็นน้ำมูกใส หรือข้นเป็นหนอง</p></li><li><p>- มีเลือดออกจากจมูกเล็กน้อยหรือมาก</p></li><li><p>- คัดจมูกและจามบ่อย</p></li><li><p>- เจ็บหรือระคายเคืองภายในโพรงจมูก</p></li><li><p>- อาจมีอาการอื่นร่วม เช่น ปวดหัว หรือปวดใบหน้า</p></li></ul>'),
(31, '<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- มีแผลเปิดหรือเลือดออก</p></li><li><p>- ปวดหรือบาดเจ็บที่ศีรษะ คอ หรือหลัง</p></li><li><p>- ไม่รู้สึกตัวหรือรู้สึกสับสน</p></li><li><p>- หายใจลำบากหรือหายใจผิดปกติ</p></li><li><p>- แขนขาเคลื่อนไหวไม่ได้ หรือมีอาการชา</p></li><li><p>- ช็อก (ผิวซีด เหงื่อออก ตัวเย็น)</p></li></ul>'),
(32, '<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- ปวดหรือบาดเจ็บที่ศีรษะ คอ หรือหลัง</p></li><li><p>- มีแผลเปิดหรือเลือดออก</p></li><li><p>- ไม่รู้สึกตัว หรือสับสน</p></li><li><p>- หายใจลำบาก หรือหายใจผิดปกติ</p></li><li><p>- แขนขาเคลื่อนไหวไม่ได้ หรือชาร่วมด้วย</p></li><li><p>- มีอาการช็อก เช่น ตัวซีด เหงื่อออก ตัวเย็น</p></li></ul>'),
(33, '<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- ปวด บวม แดง หรือฟกช้ำบริเวณที่ได้รับบาดเจ็บ</p></li><li><p>- ข้อเคลื่อนไม่ได้ หรือเคลื่อนไหวลำบาก</p></li><li><p>- รู้สึกอ่อนแรงหรือชา</p></li><li><p>- มีเสียง \"เป๊าะ\" หรือ \"ดังกร๊อบ\" ขณะบาดเจ็บ</p></li><li><p>- หากบาดเจ็บรุนแรง เช่น กระดูกหักหรือศีรษะกระแทก อาจหมดสติหรือมีอาการคลื่นไส้อาเจียนร่วมด้วย</p></li></ul>'),
(34, '<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- เจ็บคอหรือระคายเคืองในลำคอ</p></li><li><p>- รู้สึกเหมือนมีสิ่งแปลกปลอมติดอยู่</p></li><li><p>- กลืนลำบากหรือเจ็บขณะกลืน</p></li><li><p>- อาจมีเลือดออกเล็กน้อยในบางกรณี</p></li><li><p>- หากปล่อยไว้นาน อาจเกิดบวม อักเสบ หรือเป็นไข้ได้</p></li></ul>'),
(35, '<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- มีแผลเปิดหรือแผลลึก</p></li><li><p>- เลือดออกจากแผลมากหรือน้อย ขึ้นอยู่กับความลึก</p></li><li><p>- อาจรู้สึกปวดหรือแสบ</p></li><li><p>- หากแผลลึกอาจเห็นกล้ามเนื้อหรือเนื้อเยื่อภายใน</p></li><li><p>- อาจมีอาการบวม แดง หรือมีหนอง หากติดเชื้อ</p></li></ul>');

